//Macro implementations


void FCM_own_spi()
{

	//csn
	//Output: 0 -> C2
	trisc = trisc & 0xFB;
	if ((0))
		portc = (portc & 0xFB) | 0x04;
	else
		portc = portc & 0xFB;

	//Output
	//Output: 1 -> C5
	trisc = trisc & 0xDF;
	if ((1))
		portc = (portc & 0xDF) | 0x20;
	else
		portc = portc & 0xDF;

	//clk
	//Output: 0 -> C3
	trisc = trisc & 0xF7;
	if ((0))
		portc = (portc & 0xF7) | 0x08;
	else
		portc = portc & 0xF7;

	//Calculation
	//Calculation:
	//  spi_ihap = 0
	FCV_SPI_IHAP = 0;

	//Loop
	//Loop: Loop 8 times
	for (FCLV_LOOP2=0; FCLV_LOOP2<8; FCLV_LOOP2++)
	{

		//Switch
		//Switch: spi_ihap?
		switch (FCV_SPI_IHAP)
		{
			case 1:
			{
				//Calculation
				//Calculation:
				//  spb1 = ospi_dataout AND 1
				FCV_SPB1 = FCV_OSPI_DATAOUT & 1;

				break;
			}
			case 2:
			{
				//Calculation
				//Calculation:
				//  spb2 = ospi_dataout AND 1
				FCV_SPB2 = FCV_OSPI_DATAOUT & 1;

				break;
			}
			case 3:
			{
				//Calculation
				//Calculation:
				//  spb3 = ospi_dataout AND 1
				FCV_SPB3 = FCV_OSPI_DATAOUT & 1;

				break;
			}
			case 4:
			{
				//Calculation
				//Calculation:
				//  spb4 = ospi_dataout AND 1
				FCV_SPB4 = FCV_OSPI_DATAOUT & 1;

				break;
			}
			case 5:
			{
				//Calculation
				//Calculation:
				//  spb5 = ospi_dataout AND 1
				FCV_SPB5 = FCV_OSPI_DATAOUT & 1;

				break;
			}
			case 6:
			{
				//Calculation
				//Calculation:
				//  spb6 = ospi_dataout AND 1
				FCV_SPB6 = FCV_OSPI_DATAOUT & 1;

				break;
			}
			case 7:
			{
				//Calculation
				//Calculation:
				//  spb7 = ospi_dataout AND 1
				FCV_SPB7 = FCV_OSPI_DATAOUT & 1;

				break;
			}
			case 0:
			{
				//Calculation
				//Calculation:
				//  spb0 = ospi_dataout AND 1
				FCV_SPB0 = FCV_OSPI_DATAOUT & 1;

				break;
			}
			// default:

		}

		//Calculation
		//Calculation:
		//  ospi_dataout = ospi_dataout >> 1
		//  spi_ihap = spi_ihap + 1
		FCV_OSPI_DATAOUT = FCV_OSPI_DATAOUT >> 1;
		FCV_SPI_IHAP = FCV_SPI_IHAP + 1;


	}

	//Calculation
	//Calculation:
	//  spi_ihap = 0
	FCV_SPI_IHAP = 0;

	//Loop
	//Loop: Loop 8 times
	for (FCLV_LOOP3=0; FCLV_LOOP3<8; FCLV_LOOP3++)
	{

		//Switch
		//Switch: spi_ihap?
		switch (FCV_SPI_IHAP)
		{
			case 1:
			{
				//Output
				//Output: spb6 -> C5
				trisc = trisc & 0xDF;
				if ((FCV_SPB6))
					portc = (portc & 0xDF) | 0x20;
				else
					portc = portc & 0xDF;

				break;
			}
			case 2:
			{
				//Output
				//Output: spb5 -> C5
				trisc = trisc & 0xDF;
				if ((FCV_SPB5))
					portc = (portc & 0xDF) | 0x20;
				else
					portc = portc & 0xDF;

				break;
			}
			case 3:
			{
				//Output
				//Output: spb4 -> C5
				trisc = trisc & 0xDF;
				if ((FCV_SPB4))
					portc = (portc & 0xDF) | 0x20;
				else
					portc = portc & 0xDF;

				break;
			}
			case 4:
			{
				//Output
				//Output: spb3 -> C5
				trisc = trisc & 0xDF;
				if ((FCV_SPB3))
					portc = (portc & 0xDF) | 0x20;
				else
					portc = portc & 0xDF;

				break;
			}
			case 5:
			{
				//Output
				//Output: spb2 -> C5
				trisc = trisc & 0xDF;
				if ((FCV_SPB2))
					portc = (portc & 0xDF) | 0x20;
				else
					portc = portc & 0xDF;

				break;
			}
			case 6:
			{
				//Output
				//Output: spb1 -> C5
				trisc = trisc & 0xDF;
				if ((FCV_SPB1))
					portc = (portc & 0xDF) | 0x20;
				else
					portc = portc & 0xDF;

				break;
			}
			case 7:
			{
				//Output
				//Output: spb0 -> C5
				trisc = trisc & 0xDF;
				if ((FCV_SPB0))
					portc = (portc & 0xDF) | 0x20;
				else
					portc = portc & 0xDF;

				break;
			}
			case 0:
			{
				//Output
				//Output: spb7 -> C5
				trisc = trisc & 0xDF;
				if ((FCV_SPB7))
					portc = (portc & 0xDF) | 0x20;
				else
					portc = portc & 0xDF;

				break;
			}
			// default:

		}

		//clk
		//Output: 1 -> C3
		trisc = trisc & 0xF7;
		if ((1))
			portc = (portc & 0xF7) | 0x08;
		else
			portc = portc & 0xF7;

		//Delay
		//Delay: 5 us
		delay_us(5);

		//clk
		//Output: 0 -> C3
		trisc = trisc & 0xF7;
		if ((0))
			portc = (portc & 0xF7) | 0x08;
		else
			portc = portc & 0xF7;

		//Delay
		//Delay: 5 us
		delay_us(5);

		//Calculation
		//Calculation:
		//  spi_ihap = spi_ihap + 1
		FCV_SPI_IHAP = FCV_SPI_IHAP + 1;


	}

	//Output
	//Output: 1 -> C5
	trisc = trisc & 0xDF;
	if ((1))
		portc = (portc & 0xDF) | 0x20;
	else
		portc = portc & 0xDF;

	//Output
	//Output: 1 -> C3
	trisc = trisc & 0xF7;
	if ((1))
		portc = (portc & 0xF7) | 0x08;
	else
		portc = portc & 0xF7;

	//csn
	//Output: 1 -> C2
	trisc = trisc & 0xFB;
	if ((1))
		portc = (portc & 0xFB) | 0x04;
	else
		portc = portc & 0xFB;

}


