void main()
{
	//Initialization
	adcon1 = 0x07;


	//Interrupt initialization code
	option_reg = 0xC0;


	//Call Component Macro
	//Call Component Macro: Start()
	FCD_LCDDisplay0_Start();

	//Call Component Macro
	//Call Component Macro: Clear()
	FCD_LCDDisplay0_Clear();

	//clk
	//Output: 1 -> C3
	trisc = trisc & 0xF7;
	if ((1))
		portc = (portc & 0xF7) | 0x08;
	else
		portc = portc & 0xF7;

	//csn
	//Output: 1 -> C2
	trisc = trisc & 0xFB;
	if ((1))
		portc = (portc & 0xFB) | 0x04;
	else
		portc = portc & 0xFB;

	//ce
	//Output: 1 -> C1
	trisc = trisc & 0xFD;
	if ((1))
		portc = (portc & 0xFD) | 0x02;
	else
		portc = portc & 0xFD;

	//setup
	//Loop: Loop 1 times
	for (FCLV_LOOP1=0; FCLV_LOOP1<1; FCLV_LOOP1++)
	{

		//Call Macro
		//Call Macro: rf_begin()
		FCM_rf_begin();

		//Call Macro
		//Call Macro: set_writing_pipe0()
		FCM_set_writing_pipe0();

		//Call Macro
		//Call Macro: open_writing_pipe_address()
		FCM_open_writing_pipe_address();

		//Call Macro
		//Call Macro: rf_setpa()
		FCM_rf_setpa();

		//Call Macro
		//Call Macro: activate()
		FCM_activate();


	}

	//Delay
	//Delay: 1 ms
	delay_ms(1);

	//Loop
	//Loop: While 1
	while (1)
	{

		//Call Macro
		//Call Macro: radio_writepayload()
		FCM_radio_writepayload();

		//Call Macro
		//Call Macro: tx_flush()
		FCM_tx_flush();

		//Delay
		//Delay: 1 ms
		delay_ms(1);


	}

	mainendloop: goto mainendloop;
}

