void main()
{
	//Initialization
	ansel = 0;
anselh = 0;


	//Interrupt initialization code
	option_reg = 0xC0;


	//Calculation
	//Calculation:
	//  FB1 = 0
	//  FB2 = 0
	//  FB3 = 0
	//  FB4 = 1
	//  DUTYCYCLE = 65
	FCV_FB1 = 0;
	FCV_FB2 = 0;
	FCV_FB3 = 0;
	FCV_FB4 = 1;
	FCV_DUTYCYCLE = 65;

	//RELAY 1
	//Output: 1 -> C4
	trisc = trisc & 0xEF;
	if ((1))
		portc = (portc & 0xEF) | 0x10;
	else
		portc = portc & 0xEF;

	//RELAY 2
	//Output: 1 -> C6
	trisc = trisc & 0xBF;
	if ((1))
		portc = (portc & 0xBF) | 0x40;
	else
		portc = portc & 0xBF;

	//RELAY 3
	//Output: 1 -> C7
	trisc = trisc & 0x7F;
	if ((1))
		portc = (portc & 0x7F) | 0x80;
	else
		portc = portc & 0x7F;

	//Output
	//Output: 0 -> C5
	trisc = trisc & 0xDF;
	if ((0))
		portc = (portc & 0xDF) | 0x20;
	else
		portc = portc & 0xDF;

	//Delay
	//Delay: 1 s
	delay_s(1);

	//Loop
	//Loop: While 1
	while (1)
	{

		//Input
		//Input: C0 -> TP0
		trisc = trisc | 0x01;
		FCV_TP0 = ((portc & 0x01) == 0x01);

		//Input
		//Input: C1 -> TP1
		trisc = trisc | 0x02;
		FCV_TP1 = ((portc & 0x02) == 0x02);

		//Input
		//Input: C2 -> TP2
		trisc = trisc | 0x04;
		FCV_TP2 = ((portc & 0x04) == 0x04);

		//Input
		//Input: C3 -> TP3
		trisc = trisc | 0x08;
		FCV_TP3 = ((portc & 0x08) == 0x08);

		//Decision
		//Decision: TP0 = 0?
		if (FCV_TP0 == 0)
		{

			//Loop
			//Loop: While TP0 = 0
			while (FCV_TP0 == 0)
			{

				//Input
				//Input: C0 -> TP0
				trisc = trisc | 0x01;
				FCV_TP0 = ((portc & 0x01) == 0x01);


			}

			//Switch
			//Switch: FB1?
			switch (FCV_FB1)
			{
				case 0:
				{
					//RELAY 3
					//Output: 0 -> C7
					trisc = trisc & 0x7F;
					if ((0))
						portc = (portc & 0x7F) | 0x80;
					else
						portc = portc & 0x7F;

					//Calculation
					//Calculation:
					//  FB1 = 1
					FCV_FB1 = 1;

					break;
				}
				case 1:
				{
					//RELAY 3
					//Output: 1 -> C7
					trisc = trisc & 0x7F;
					if ((1))
						portc = (portc & 0x7F) | 0x80;
					else
						portc = portc & 0x7F;

					//Calculation
					//Calculation:
					//  FB1 = 0
					FCV_FB1 = 0;

					break;
				}
				// default:

			}

		// } else {

		}

		//Decision
		//Decision: TP1 = 0?
		if (FCV_TP1 == 0)
		{

			//Loop
			//Loop: While TP1 = 0
			while (FCV_TP1 == 0)
			{

				//Input
				//Input: C1 -> TP1
				trisc = trisc | 0x02;
				FCV_TP1 = ((portc & 0x02) == 0x02);


			}

			//Switch
			//Switch: FB2?
			switch (FCV_FB2)
			{
				case 0:
				{
					//RELAY 2
					//Output: 0 -> C6
					trisc = trisc & 0xBF;
					if ((0))
						portc = (portc & 0xBF) | 0x40;
					else
						portc = portc & 0xBF;

					//Calculation
					//Calculation:
					//  FB2 = 1
					FCV_FB2 = 1;

					break;
				}
				case 1:
				{
					//RELAY 2
					//Output: 1 -> C6
					trisc = trisc & 0xBF;
					if ((1))
						portc = (portc & 0xBF) | 0x40;
					else
						portc = portc & 0xBF;

					//Calculation
					//Calculation:
					//  FB2 = 0
					FCV_FB2 = 0;

					break;
				}
				// default:

			}

		// } else {

		}

		//Decision
		//Decision: TP2 = 0?
		if (FCV_TP2 == 0)
		{

			//Loop
			//Loop: While TP2 = 0
			while (FCV_TP2 == 0)
			{

				//Input
				//Input: C2 -> TP2
				trisc = trisc | 0x04;
				FCV_TP2 = ((portc & 0x04) == 0x04);


			}

			//Switch
			//Switch: FB3?
			switch (FCV_FB3)
			{
				case 0:
				{
					//RELAY 1
					//Output: 0 -> C4
					trisc = trisc & 0xEF;
					if ((0))
						portc = (portc & 0xEF) | 0x10;
					else
						portc = portc & 0xEF;

					//Calculation
					//Calculation:
					//  FB3 = 1
					FCV_FB3 = 1;

					break;
				}
				case 1:
				{
					//RELAY 1
					//Output: 1 -> C4
					trisc = trisc & 0xEF;
					if ((1))
						portc = (portc & 0xEF) | 0x10;
					else
						portc = portc & 0xEF;

					//Calculation
					//Calculation:
					//  FB3 = 0
					FCV_FB3 = 0;

					break;
				}
				// default:

			}

		// } else {

		}

		//Decision
		//Decision: TP3 = 0?
		if (FCV_TP3 == 0)
		{

			//Loop
			//Loop: While TP3 = 0
			while (FCV_TP3 == 0)
			{

				//Input
				//Input: C3 -> TP3
				trisc = trisc | 0x08;
				FCV_TP3 = ((portc & 0x08) == 0x08);


			}

			//Switch
			//Switch: FB4?
			switch (FCV_FB4)
			{
				case 0:
				{
					//Calculation
					//Calculation:
					//  FB4 = 1
					FCV_FB4 = 1;

					//Call Component Macro
					//Call Component Macro: Enable()
					FCD_PWM0_Enable();

					break;
				}
				case 1:
				{
					//Calculation
					//Calculation:
					//  FB4 = 0
					FCV_FB4 = 0;

					//Call Component Macro
					//Call Component Macro: Disable()
					FCD_PWM0_Disable();

					//Output
					//Output: 0 -> C5
					trisc = trisc & 0xDF;
					if ((0))
						portc = (portc & 0xDF) | 0x20;
					else
						portc = portc & 0xDF;

					break;
				}
				// default:

			}

		// } else {

		}

		//Input
		//Input: B7 -> TP4
		trisb = trisb | 0x80;
		FCV_TP4 = ((portb & 0x80) == 0x80);

		//Input
		//Input: B6 -> TP5
		trisb = trisb | 0x40;
		FCV_TP5 = ((portb & 0x40) == 0x40);

		//Input
		//Input: B5 -> TP6
		trisb = trisb | 0x20;
		FCV_TP6 = ((portb & 0x20) == 0x20);

		//Input
		//Input: B4 -> TP7
		trisb = trisb | 0x10;
		FCV_TP7 = ((portb & 0x10) == 0x10);

		//Decision
		//Decision: TP4 = 0 AND FB4 = 1?
		if (FCV_TP4 == 0 & FCV_FB4 == 1)
		{

			//Call Component Macro
			//Call Component Macro: SetDutyCycle(65)
			FCD_PWM0_SetDutyCycle(65);

			//Loop
			//Loop: While TP4 = 0
			while (FCV_TP4 == 0)
			{

				//Input
				//Input: B7 -> TP4
				trisb = trisb | 0x80;
				FCV_TP4 = ((portb & 0x80) == 0x80);


			}

		// } else {

		}

		//Decision
		//Decision: TP5 = 0 AND FB4 = 1?
		if (FCV_TP5 == 0 & FCV_FB4 == 1)
		{

			//Call Component Macro
			//Call Component Macro: SetDutyCycle(130)
			FCD_PWM0_SetDutyCycle(130);

			//Loop
			//Loop: While TP5 = 0
			while (FCV_TP5 == 0)
			{

				//Input
				//Input: B6 -> TP5
				trisb = trisb | 0x40;
				FCV_TP5 = ((portb & 0x40) == 0x40);


			}

		// } else {

		}

		//Decision
		//Decision: TP6 = 0 AND FB4 = 1?
		if (FCV_TP6 == 0 & FCV_FB4 == 1)
		{

			//Call Component Macro
			//Call Component Macro: SetDutyCycle(195)
			FCD_PWM0_SetDutyCycle(195);

			//Loop
			//Loop: While TP6 = 0
			while (FCV_TP6 == 0)
			{

				//Input
				//Input: B5 -> TP6
				trisb = trisb | 0x20;
				FCV_TP6 = ((portb & 0x20) == 0x20);


			}

		// } else {

		}

		//Decision
		//Decision: TP7 = 0 AND FB4 = 1?
		if (FCV_TP7 == 0 & FCV_FB4 == 1)
		{

			//Call Component Macro
			//Call Component Macro: SetDutyCycle(255)
			FCD_PWM0_SetDutyCycle(255);

			//Loop
			//Loop: While TP7 = 0
			while (FCV_TP7 == 0)
			{

				//Input
				//Input: B4 -> TP7
				trisb = trisb | 0x10;
				FCV_TP7 = ((portb & 0x10) == 0x10);


			}

		// } else {

		}


	}

	mainendloop: goto mainendloop;
}

