void FCM_display_temp()
{

	//Calculation
	//Calculation:
	//  temp_tens = temp_c / 10
	//  temp_ones = temp_c MOD 10
	//  temp_dec = temp_cdp
	FCV_TEMP_TENS = FCV_TEMP_C / 10;
	FCV_TEMP_ONES = FCV_TEMP_C % 10;
	FCV_TEMP_DEC = FCV_TEMP_CDP;

	//Output
	//Output: 0b00000001 -> PORTB
	trisb = 0x00;
	portb = (1);

	//Call Macro
	//Call Macro: scan_off()
	FCM_scan_off();

	//Call Macro
	//Call Macro: scan1()
	FCM_scan1();

	//Calculation
	//Calculation:
	//  digit = temp_tens
	FCV_DIGIT = FCV_TEMP_TENS;

	//Call Macro
	//Call Macro: seg_pattern()
	FCM_seg_pattern();

	//Delay
	//Delay: 4 ms
	delay_ms(4);

	//Output
	//Output: 0b00000001 -> PORTB
	trisb = 0x00;
	portb = (1);

	//Call Macro
	//Call Macro: scan_off()
	FCM_scan_off();

	//Call Macro
	//Call Macro: scan2()
	FCM_scan2();

	//Calculation
	//Calculation:
	//  digit = temp_ones
	FCV_DIGIT = FCV_TEMP_ONES;

	//Call Macro
	//Call Macro: seg_pattern()
	FCM_seg_pattern();

	//Output
	//Output: 0 -> B0
	trisb = trisb & 0xFE;
	if ((0))
		portb = (portb & 0xFE) | 0x01;
	else
		portb = portb & 0xFE;

	//Delay
	//Delay: 4 ms
	delay_ms(4);

	//Output
	//Output: 0b00000001 -> PORTB
	trisb = 0x00;
	portb = (1);

	//Call Macro
	//Call Macro: scan_off()
	FCM_scan_off();

	//Call Macro
	//Call Macro: scan3()
	FCM_scan3();

	//Calculation
	//Calculation:
	//  digit = temp_dec
	FCV_DIGIT = FCV_TEMP_DEC;

	//Call Macro
	//Call Macro: seg_pattern()
	FCM_seg_pattern();

	//Delay
	//Delay: 4 ms
	delay_ms(4);

	//Output
	//Output: 0b00000001 -> PORTB
	trisb = 0x00;
	portb = (1);

	//Call Macro
	//Call Macro: scan_off()
	FCM_scan_off();

	//Call Macro
	//Call Macro: scan4()
	FCM_scan4();

	//Output
	//Output: 0b11100000 -> PORTB
	trisb = 0x00;
	portb = (224);

	//Delay
	//Delay: 4 ms
	delay_ms(4);

}


