void FCM_calibrate()
{

	//Call Macro
	//Call Macro: lights_off()
	FCM_lights_off();

	//Loop
	//Loop: While swg = 0 OR swr = 0
	while (FCV_SWG == 0 | FCV_SWR == 0)
	{

		//Call Macro
		//Call Macro: all_input()
		FCM_all_input();

		//Call Macro
		//Call Macro: display_temp_calib()
		FCM_display_temp_calib();


	}

	//Calculation
	//Calculation:
	//  calib_count = 0
	FCV_CALIB_COUNT = 0;

	//Loop
	//Loop: While 1
	while (1)
	{

		//Connection Point
		//Connection Point: [A]: A
FCC_calibrate_A:
;

		//Calculation
		//Calculation:
		//  reset_counter = 0
		FCV_RESET_COUNTER = 0;

		//Call Macro
		//Call Macro: all_input()
		FCM_all_input();

		//Decision
		//Decision: swg = 0 AND swr = 1?
		if (FCV_SWG == 0 & FCV_SWR == 1)
		{

			//Calculation
			//Calculation:
			//  calib_count = 0
			FCV_CALIB_COUNT = 0;

			//Calculation
			//Calculation:
			//  temp_cdp = temp_cdp + 1
			//  calib_offset = calib_offset + 1
			FCV_TEMP_CDP = FCV_TEMP_CDP + 1;
			FCV_CALIB_OFFSET = FCV_CALIB_OFFSET + 1;

			//Decision
			//Decision: calib_offset > 9?
			if (FCV_CALIB_OFFSET > 9)
			{

				//Calculation
				//Calculation:
				//  calib_offset = 0
				FCV_CALIB_OFFSET = 0;

			// } else {

			}

			//Decision
			//Decision: temp_cdp > 9?
			if (FCV_TEMP_CDP > 9)
			{

				//Calculation
				//Calculation:
				//  calib_subtractor = calib_subtractor - 1
				//  temp_c = temp_c + 1
				//  temp_cdp = 0
				FCV_CALIB_SUBTRACTOR = FCV_CALIB_SUBTRACTOR - 1;
				FCV_TEMP_C = FCV_TEMP_C + 1;
				FCV_TEMP_CDP = 0;

			// } else {

			}

			//Loop
			//Loop: While swg = 0
			while (FCV_SWG == 0)
			{

				//Call Macro
				//Call Macro: all_input()
				FCM_all_input();

				//Call Macro
				//Call Macro: display_temp_calib()
				FCM_display_temp_calib();


			}

		} else {

			//Decision
			//Decision: swg = 1 AND swr = 0?
			if (FCV_SWG == 1 & FCV_SWR == 0)
			{

				//Calculation
				//Calculation:
				//  calib_count = 0
				FCV_CALIB_COUNT = 0;

				//Calculation
				//Calculation:
				//  temp_cdp = temp_cdp - 1
				//  calib_offset = calib_offset - 1
				FCV_TEMP_CDP = FCV_TEMP_CDP - 1;
				FCV_CALIB_OFFSET = FCV_CALIB_OFFSET - 1;

				//Decision
				//Decision: calib_offset < 0?
				if (FCV_CALIB_OFFSET < 0)
				{

					//Calculation
					//Calculation:
					//  calib_offset = 9
					FCV_CALIB_OFFSET = 9;

				// } else {

				}

				//Decision
				//Decision: temp_cdp < 0?
				if (FCV_TEMP_CDP < 0)
				{

					//Calculation
					//Calculation:
					//  calib_subtractor = calib_subtractor + 1
					//  temp_c = temp_c - 1
					//  temp_cdp = 9
					FCV_CALIB_SUBTRACTOR = FCV_CALIB_SUBTRACTOR + 1;
					FCV_TEMP_C = FCV_TEMP_C - 1;
					FCV_TEMP_CDP = 9;

				// } else {

				}

				//Loop
				//Loop: While swr = 0
				while (FCV_SWR == 0)
				{

					//Call Macro
					//Call Macro: all_input()
					FCM_all_input();

					//Call Macro
					//Call Macro: display_temp_calib()
					FCM_display_temp_calib();

					//Decision
					//Decision: reset_counter = 255?
					if (FCV_RESET_COUNTER == 255)
					{

						//Call Macro
						//Call Macro: repeat()
						FCM_repeat();

						//Call Component Macro
						//Call Component Macro: Write(1, 21)
						FCD_EEPROM0_WriteEEPROM(1, 21);

						//Call Component Macro
						//Call Component Macro: Write(2, 3)
						FCD_EEPROM0_WriteEEPROM(2, 3);

						//Delay
						//Delay: 100 ms
						delay_ms(100);

						//Goto Connection Point
						//Goto Connection Point: [C]: C
						goto FCC_calibrate_C;

					} else {

						//Calculation
						//Calculation:
						//  reset_counter = reset_counter + 1
						FCV_RESET_COUNTER = FCV_RESET_COUNTER + 1;

					}


				}

			// } else {

			}

		}

		//Call Macro
		//Call Macro: display_temp_calib()
		FCM_display_temp_calib();

		//Calculation
		//Calculation:
		//  calib_count = calib_count + 1
		FCV_CALIB_COUNT = FCV_CALIB_COUNT + 1;

		//Decision
		//Decision: calib_count = 150?
		if (FCV_CALIB_COUNT == 150)
		{

			//Goto Connection Point
			//Goto Connection Point: [B]: B
			goto FCC_calibrate_B;

		// } else {

		}


	}

	//Connection Point
	//Connection Point: [B]: B
FCC_calibrate_B:
;

	//Calculation
	//Calculation:
	//  save_calib0 = calib_subtractor - 250
	//  save_calib1 = calib_offset
	FCV_SAVE_CALIB0 = FCV_CALIB_SUBTRACTOR - 250;
	FCV_SAVE_CALIB1 = FCV_CALIB_OFFSET;

	//Call Component Macro
	//Call Component Macro: Write(1, save_calib0)
	FCD_EEPROM0_WriteEEPROM(1, FCV_SAVE_CALIB0);

	//Call Component Macro
	//Call Component Macro: Write(2, save_calib1)
	FCD_EEPROM0_WriteEEPROM(2, FCV_SAVE_CALIB1);

	//Delay
	//Delay: 100 ms
	delay_ms(100);

	//Connection Point
	//Connection Point: [C]: C
FCC_calibrate_C:
;

}


void FCM_display_temp_calib()
{

	//Calculation
	//Calculation:
	//  temp_tens = temp_c / 10
	//  temp_ones = temp_c MOD 10
	//  temp_dec = temp_cdp
	FCV_TEMP_TENS = FCV_TEMP_C / 10;
	FCV_TEMP_ONES = FCV_TEMP_C % 10;
	FCV_TEMP_DEC = FCV_TEMP_CDP;

	//Output
	//Output: 0b00000001 -> PORTB
	trisb = 0x00;
	portb = (1);

	//Call Macro
	//Call Macro: scan_off()
	FCM_scan_off();

	//Call Macro
	//Call Macro: scan1()
	FCM_scan1();

	//Calculation
	//Calculation:
	//  digit = temp_tens
	FCV_DIGIT = FCV_TEMP_TENS;

	//Call Macro
	//Call Macro: seg_pattern()
	FCM_seg_pattern();

	//Delay
	//Delay: 4 ms
	delay_ms(4);

	//Output
	//Output: 0b00000001 -> PORTB
	trisb = 0x00;
	portb = (1);

	//Call Macro
	//Call Macro: scan_off()
	FCM_scan_off();

	//Call Macro
	//Call Macro: scan2()
	FCM_scan2();

	//Calculation
	//Calculation:
	//  digit = temp_ones
	FCV_DIGIT = FCV_TEMP_ONES;

	//Call Macro
	//Call Macro: seg_pattern()
	FCM_seg_pattern();

	//Output
	//Output: 0 -> B0
	trisb = trisb & 0xFE;
	if ((0))
		portb = (portb & 0xFE) | 0x01;
	else
		portb = portb & 0xFE;

	//Delay
	//Delay: 4 ms
	delay_ms(4);

	//Output
	//Output: 0b00000001 -> PORTB
	trisb = 0x00;
	portb = (1);

	//Call Macro
	//Call Macro: scan_off()
	FCM_scan_off();

	//Call Macro
	//Call Macro: scan3()
	FCM_scan3();

	//Calculation
	//Calculation:
	//  digit = temp_dec
	FCV_DIGIT = FCV_TEMP_DEC;

	//Call Macro
	//Call Macro: seg_pattern()
	FCM_seg_pattern();

	//Delay
	//Delay: 4 ms
	delay_ms(4);

	//Output
	//Output: 0b00000001 -> PORTB
	trisb = 0x00;
	portb = (1);

	//Call Macro
	//Call Macro: scan_off()
	FCM_scan_off();

	//Call Macro
	//Call Macro: scan4()
	FCM_scan4();

	//Output
	//Output: 0b11110000 -> PORTB
	trisb = 0x00;
	portb = (240);

	//Delay
	//Delay: 4 ms
	delay_ms(4);

}


