		portc = portc & 0xFB;

	//Delay
	//Delay: 5 us
	delay_us(5);

	//CSN
	//Output: 0 -> C2
	trisc = trisc & 0xFB;
	if ((0))
		portc = (portc & 0xFB) | 0x04;
	else
		portc = portc & 0xFB;

	//Delay
