void FCM_scan_row1()
{

	//row1
	//Output: 1 -> C3
	trisc = trisc & 0xF7;
	if ((1))
		portc = (portc & 0xF7) | 0x08;
	else
		portc = portc & 0xF7;

	//Delay
	//Delay: 10 ms
	delay_ms(10);

	//Input
	//Input: C0 -> col1
	trisc = trisc | 0x01;
	FCV_COL1 = ((portc & 0x01) == 0x01);

	//Input
	//Input: C1 -> col2
	trisc = trisc | 0x02;
	FCV_COL2 = ((portc & 0x02) == 0x02);

	//Input
	//Input: C2 -> col3
	trisc = trisc | 0x04;
	FCV_COL3 = ((portc & 0x04) == 0x04);

	//Delay
	//Delay: 10 ms
	delay_ms(10);

	//row1
	//Output: 0 -> C3
	trisc = trisc & 0xF7;
	if ((0))
		portc = (portc & 0xF7) | 0x08;
	else
		portc = portc & 0xF7;

}


