void main()
{
	//Initialization
	ansel = 0;
anselh = 0;


	//Interrupt initialization code
	option_reg = 0xC0;


	//Calculation
	//Calculation:
	//  col1 = 0
	//  col2 = 0
	//  col3 = 0
	//  error = 0
	FCV_COL1 = 0;
	FCV_COL2 = 0;
	FCV_COL3 = 0;
	FCV_ERROR = 0;

	//Loop
	//Loop: While 1
	while (1)
	{

		//Calculation
		//Calculation:
		//  check = 0
		FCV_CHECK = 0;

		//Call Macro
		//Call Macro: scan_row1()
		FCM_scan_row1();

		//Decision
		//Decision: col2 = 1 OR col3 = 1?
		if (FCV_COL2 == 1 | FCV_COL3 == 1)
		{

			//Call Component Macro
			//Call Component Macro: LEDOn()
			FCD_LED0_LEDOn();

			//Output
			//Output: 1 -> C7
			trisc = trisc & 0x7F;
			if ((1))
				portc = (portc & 0x7F) | 0x80;
			else
				portc = portc & 0x7F;

			//Calculation
			//Calculation:
			//  check = 0
			FCV_CHECK = 0;

		} else {

			//Decision
			//Decision: col1 = 1?
			if (FCV_COL1 == 1)
			{

				//Calculation
				//Calculation:
				//  check = check + 1
				FCV_CHECK = FCV_CHECK + 1;

			} else {

				//Calculation
				//Calculation:
				//  check = 0
				FCV_CHECK = 0;

			}

			//Call Macro
			//Call Macro: scan_row2()
			FCM_scan_row2();

			//Decision
			//Decision: col1 = 1 OR col3 = 1?
			if (FCV_COL1 == 1 | FCV_COL3 == 1)
			{

				//Call Component Macro
				//Call Component Macro: LEDOn()
				FCD_LED0_LEDOn();

				//Output
				//Output: 1 -> C7
				trisc = trisc & 0x7F;
				if ((1))
					portc = (portc & 0x7F) | 0x80;
				else
					portc = portc & 0x7F;

				//Calculation
				//Calculation:
				//  check = 0
				FCV_CHECK = 0;

			} else {

				//Decision
				//Decision: col2 = 1?
				if (FCV_COL2 == 1)
				{

					//Calculation
					//Calculation:
					//  check = check + 1
					FCV_CHECK = FCV_CHECK + 1;

				} else {

					//Calculation
					//Calculation:
					//  check = 0
					FCV_CHECK = 0;

				}

				//Call Macro
				//Call Macro: scan_row3()
				FCM_scan_row3();

				//Decision
				//Decision: col1 = 1 OR col2 = 1 OR col3 = 1?
				if (FCV_COL1 == 1 | FCV_COL2 == 1 | FCV_COL3 == 1)
				{

					//Call Component Macro
					//Call Component Macro: LEDOn()
					FCD_LED0_LEDOn();

					//Output
					//Output: 1 -> C7
					trisc = trisc & 0x7F;
					if ((1))
						portc = (portc & 0x7F) | 0x80;
					else
						portc = portc & 0x7F;

					//Calculation
					//Calculation:
					//  check = 0
					FCV_CHECK = 0;

				} else {

					//Call Component Macro
					//Call Component Macro: LEDOff()
					FCD_LED0_LEDOff();

					//Output
					//Output: 0 -> C7
					trisc = trisc & 0x7F;
					if ((0))
						portc = (portc & 0x7F) | 0x80;
					else
						portc = portc & 0x7F;

					//Decision
					//Decision: check = 2?
					if (FCV_CHECK == 2)
					{

						//Call Component Macro
						//Call Component Macro: LEDOn()
						FCD_LED1_LEDOn();

						//Output
						//Output: 1 -> C6
						trisc = trisc & 0xBF;
						if ((1))
							portc = (portc & 0xBF) | 0x40;
						else
							portc = portc & 0xBF;

					} else {

						//Call Component Macro
						//Call Component Macro: LEDOff()
						FCD_LED1_LEDOff();

						//Output
						//Output: 0 -> C6
						trisc = trisc & 0xBF;
						if ((0))
							portc = (portc & 0xBF) | 0x40;
						else
							portc = portc & 0xBF;

					}

				}

			}

		}


	}

	mainendloop: goto mainendloop;
}

