void FCM_display()
{

	//Call Macro
	//Call Macro: stored_values()
	FCM_stored_values();

	//OE
	//Output: 0 -> C5
	trisc = trisc & 0xDF;
	if ((0))
		portc = (portc & 0xDF) | 0x20;
	else
		portc = portc & 0xDF;

	//CLK
	//Output: 1 -> C3
	trisc = trisc & 0xF7;
	if ((1))
		portc = (portc & 0xF7) | 0x08;
	else
		portc = portc & 0xF7;

	//REG
	//Output: 0 -> C4
	trisc = trisc & 0xEF;
	if ((0))
		portc = (portc & 0xEF) | 0x10;
	else
		portc = portc & 0xEF;

	//Loop
	//Loop: Loop 8 times
	for (FCLV_LOOP1=0; FCLV_LOOP1<8; FCLV_LOOP1++)
	{

		//Calculation
		//Calculation:
		//  data_out = data AND 1
		//  data = data >> 1
		FCV_DATA_OUT = FCV_DATA & 1;
		FCV_DATA = FCV_DATA >> 1;

		//DAT
		//Output: data_out -> C7
		trisc = trisc & 0x7F;
		if ((FCV_DATA_OUT))
			portc = (portc & 0x7F) | 0x80;
		else
			portc = portc & 0x7F;

		//CLK
		//Output: 0 -> C3
		trisc = trisc & 0xF7;
		if ((0))
			portc = (portc & 0xF7) | 0x08;
		else
			portc = portc & 0xF7;

		//Delay
		//Delay: 100 us
		delay_us(100);

		//CLK
		//Output: 1 -> C3
		trisc = trisc & 0xF7;
		if ((1))
			portc = (portc & 0xF7) | 0x08;
		else
			portc = portc & 0xF7;


	}

	//REG
	//Output: 1 -> C4
	trisc = trisc & 0xEF;
	if ((1))
		portc = (portc & 0xEF) | 0x10;
	else
		portc = portc & 0xEF;

	//Delay
	//Delay: 100 us
	delay_us(100);

	//REG
	//Output: 0 -> C4
	trisc = trisc & 0xEF;
	if ((0))
		portc = (portc & 0xEF) | 0x10;
	else
		portc = portc & 0xEF;

}


