void FCM_show_analog_read()
{

	//Output
	//Output: 0 -> A4
	trisa = trisa & 0xEF;
	if ((0))
		porta = (porta & 0xEF) | 0x10;
	else
		porta = porta & 0xEF;

	//Output
	//Output: 1 -> A5
	trisa = trisa & 0xDF;
	if ((1))
		porta = (porta & 0xDF) | 0x20;
	else
		porta = porta & 0xDF;

	//Calculation
	//Calculation:
	//  hundreds = analog_read / 100
	//  tens = (analog_read MOD 100) / 10
	//  ones = analog_read MOD 10
	FCV_HUNDREDS = FCV_ANALOG_READ / 100;
	FCV_TENS = (FCV_ANALOG_READ % 100) / 10;
	FCV_ONES = FCV_ANALOG_READ % 10;

	//Calculation
	//Calculation:
	//  digit = hundreds
	FCV_DIGIT = FCV_HUNDREDS;

	//Call Macro
	//Call Macro: display()
	FCM_display();

	//Delay
	//Delay: 400 ms
	delay_ms(255);
	delay_ms(145);

	//Calculation
	//Calculation:
	//  digit = tens
	FCV_DIGIT = FCV_TENS;

	//Call Macro
	//Call Macro: display()
	FCM_display();

	//Delay
	//Delay: 400 ms
	delay_ms(255);
	delay_ms(145);

	//Calculation
	//Calculation:
	//  digit = ones
	FCV_DIGIT = FCV_ONES;

	//Call Macro
	//Call Macro: display()
	FCM_display();

	//Delay
	//Delay: 400 ms
	delay_ms(255);
	delay_ms(145);

	//Call Macro
	//Call Macro: clear()
	FCM_clear();

}



