void FCM_reg_all()
{

	//Loop
	//Loop: Loop 8 times
	for (FCLV_LOOP4=0; FCLV_LOOP4<8; FCLV_LOOP4++)
	{

		//Calculation
		//Calculation:
		//  dat = bits AND 1
		//  dat2 = bits2 AND 1
		//  dat3 = bits3 AND 1
		FCV_DAT = FCV_BITS & 1;
		FCV_DAT2 = FCV_BITS2 & 1;
		FCV_DAT3 = FCV_BITS3 & 1;

		//time_data
		//Output: dat -> C2
		trisc = trisc & 0xFB;
		if ((FCV_DAT))
			portc = (portc & 0xFB) | 0x04;
		else
			portc = portc & 0xFB;

		//foulperiod_data
		//Output: dat2 -> C4
		trisc = trisc & 0xEF;
		if ((FCV_DAT2))
			portc = (portc & 0xEF) | 0x10;
		else
			portc = portc & 0xEF;

		//score_data
		//Output: dat3 -> C3
		trisc = trisc & 0xF7;
		if ((FCV_DAT3))
			portc = (portc & 0xF7) | 0x08;
		else
			portc = portc & 0xF7;

		//clk
		//Output: 1 -> C0
		trisc = trisc & 0xFE;
		if ((1))
			portc = (portc & 0xFE) | 0x01;
		else
			portc = portc & 0xFE;

		//clk
		//Output: 0 -> C0
		trisc = trisc & 0xFE;
		if ((0))
			portc = (portc & 0xFE) | 0x01;
		else
			portc = portc & 0xFE;

		//Calculation
		//Calculation:
		//  bits = bits >> 1
		//  bits2 = bits2 >> 1
		//  bits3 = bits3 >> 1
		FCV_BITS = FCV_BITS >> 1;
		FCV_BITS2 = FCV_BITS2 >> 1;
		FCV_BITS3 = FCV_BITS3 >> 1;


	}

}


void FCM_display_all()
{

	//Decision
	//Decision: flag = 1?
	if (FCV_FLAG == 1)
	{

		//Calculation
		//Calculation:
		//  flag = 0
		FCV_FLAG = 0;

		//Call Macro
		//Call Macro: off_all()
		FCM_off_all();

		//Calculation
		//Calculation:
		//  digit = minute_ones
		//  digitB = away_foul
		//  digitC = away_ones
		FCV_DIGIT = FCV_MINUTE_ONES;
		FCV_DIGITB = FCV_AWAY_FOUL;
		FCV_DIGITC = FCV_AWAY_ONES;

		//Call Macro
		//Call Macro: data_bank()
		FCM_data_bank();

		//Call Macro
		//Call Macro: reg_all()
		FCM_reg_all();

		//Calculation
		//Calculation:
		//  digit = seconds_ones
		//  digitB = period
		//  digitC = home_ones
		FCV_DIGIT = FCV_SECONDS_ONES;
		FCV_DIGITB = FCV_PERIOD;
		FCV_DIGITC = FCV_HOME_ONES;

		//Call Macro
		//Call Macro: data_bank()
		FCM_data_bank();

		//Call Macro
		//Call Macro: reg_all()
		FCM_reg_all();

		//Call Macro
		//Call Macro: latch()
		FCM_latch();

		//Digit2
		//Output: 0 -> D0
		trisd = trisd & 0xFE;
		if ((0))
			portd = (portd & 0xFE) | 0x01;
		else
			portd = portd & 0xFE;

		//Digit1
		//Output: 1 -> D1
		trisd = trisd & 0xFD;
		if ((1))
			portd = (portd & 0xFD) | 0x02;
		else
			portd = portd & 0xFD;

		//Delay
		//Delay: 1 ms
		delay_ms(1);

		//Comment:
		//END of ALL DIGIT1

		//Comment:
		//END of ALL DIGIT2

	} else {

		//Calculation
		//Calculation:
		//  flag = 1
		FCV_FLAG = 1;

		//Call Macro
		//Call Macro: off_all()
		FCM_off_all();

		//Calculation
		//Calculation:
		//  digit = minute_tens
		//  digitB = home_foul
		//  digitC = away_tens
		FCV_DIGIT = FCV_MINUTE_TENS;
		FCV_DIGITB = FCV_HOME_FOUL;
		FCV_DIGITC = FCV_AWAY_TENS;

		//Call Macro
		//Call Macro: data_bank()
		FCM_data_bank();

		//Call Macro
		//Call Macro: reg_all()
		FCM_reg_all();

		//Calculation
		//Calculation:
		//  digit = seconds_tens
		//  digitB = period
		//  digitC = home_tens
		FCV_DIGIT = FCV_SECONDS_TENS;
		FCV_DIGITB = FCV_PERIOD;
		FCV_DIGITC = FCV_HOME_TENS;

		//Call Macro
		//Call Macro: data_bank()
		FCM_data_bank();

		//Call Macro
		//Call Macro: reg_all()
		FCM_reg_all();

		//Call Macro
		//Call Macro: latch()
		FCM_latch();

		//Digit2
		//Output: 1 -> D0
		trisd = trisd & 0xFE;
		if ((1))
			portd = (portd & 0xFE) | 0x01;
		else
			portd = portd & 0xFE;

		//Digit1
		//Output: 0 -> D1
		trisd = trisd & 0xFD;
		if ((0))
			portd = (portd & 0xFD) | 0x02;
		else
			portd = portd & 0xFD;

		//Delay
		//Delay: 1 ms
		delay_ms(1);

	}

}



void main()
{
	//Initialization
	ansel = 0;
anselh = 0;

	
		RS232_3_UART_Init( );		//Call initialise function


	//Interrupt initialization code
	option_reg = 0xC0;


	//Home_ONE
	//Output: 0 -> D2
	trisd = trisd & 0xFB;
	if ((0))
		portd = (portd & 0xFB) | 0x04;
	else
		portd = portd & 0xFB;

	//Guest_ONE
	//Output: 0 -> D3
	trisd = trisd & 0xF7;
	if ((0))
		portd = (portd & 0xF7) | 0x08;
	else
		portd = portd & 0xF7;

	//clk
	//Output: 0 -> C0
	trisc = trisc & 0xFE;
	if ((0))
		portc = (portc & 0xFE) | 0x01;
	else
		portc = portc & 0xFE;

	//reg
	//Output: 0 -> C1
	trisc = trisc & 0xFD;
	if ((0))
		portc = (portc & 0xFD) | 0x02;
	else
		portc = portc & 0xFD;

	//Digit2
	//Output: 0 -> D0
	trisd = trisd & 0xFE;
	if ((0))
		portd = (portd & 0xFE) | 0x01;
	else
		portd = portd & 0xFE;

	//Digit1
	//Output: 1 -> D1
	trisd = trisd & 0xFD;
	if ((1))
		portd = (portd & 0xFD) | 0x02;
	else
		portd = portd & 0xFD;

	//Dot
	//Output: 0 -> D5
	trisd = trisd & 0xDF;
	if ((0))
		portd = (portd & 0xDF) | 0x20;
	else
		portd = portd & 0xDF;

	//Colon
	//Output: 1 -> D4
	trisd = trisd & 0xEF;
	if ((1))
		portd = (portd & 0xEF) | 0x10;
	else
		portd = portd & 0xEF;

	//home_one
	//Output: 1 -> D2
	trisd = trisd & 0xFB;
	if ((1))
		portd = (portd & 0xFB) | 0x04;
	else
		portd = portd & 0xFB;

	//away_one
	//Output: 1 -> D3
	trisd = trisd & 0xF7;
	if ((1))
		portd = (portd & 0xF7) | 0x08;
	else
		portd = portd & 0xF7;

	//Loop
	//Loop: While 1
	while (1)
	{

		//Call Macro
		//Call Macro: display_all()
		FCM_display_all();

		//Call Component Macro
		//Call Component Macro: header=ReceiveRS232Char(1)
		FCV_HEADER = FCD_RS2320_ReceiveRS232Char(1);

		//Decision
		//Decision: header = ':'?
		if (FCV_HEADER == ':')
		{

			//Call Component Macro
			//Call Component Macro: recv=ReceiveRS232String(1, 18)
			FCD_RS2320_ReceiveRS232String(FCV_RECV,FCSZ_RECV, 1, 18);

			//Calculation
			//Calculation:
			//  string_minutes = Right$ (recv,17)
			//  string_seconds = Right$ (recv,14)
			//  string_foulperiod = Right$ (recv,11)
			//  string_score_away = Right$ (recv,7)
			//  string_score_home = Right$ (recv,3)
			FCI_RIGHTSTRING(FCV_RECV, FCSZ_RECV, 17, FCV_STRING_MINUTES,FCSZ_STRING_MINUTES);
			FCI_RIGHTSTRING(FCV_RECV, FCSZ_RECV, 14, FCV_STRING_SECONDS,FCSZ_STRING_SECONDS);
			FCI_RIGHTSTRING(FCV_RECV, FCSZ_RECV, 11, FCV_STRING_FOULPERIOD,FCSZ_STRING_FOULPERIOD);
			FCI_RIGHTSTRING(FCV_RECV, FCSZ_RECV, 7, FCV_STRING_SCORE_AWAY,FCSZ_STRING_SCORE_AWAY);
			FCI_RIGHTSTRING(FCV_RECV, FCSZ_RECV, 3, FCV_STRING_SCORE_HOME,FCSZ_STRING_SCORE_HOME);

			//Calculation
			//Calculation:
			//  score_home = StringToInt$ (string_score_away)
			//  score_away = StringToInt$ (string_score_home)
			//  foulperiod = StringToInt$ (string_foulperiod)
			//  minutes = StringToInt$ (string_minutes)
			//  seconds = StringToInt$ (string_seconds)
			FCV_SCORE_HOME = FCI_STRING_TO_INT(FCV_STRING_SCORE_AWAY, FCSZ_STRING_SCORE_AWAY);
			FCV_SCORE_AWAY = FCI_STRING_TO_INT(FCV_STRING_SCORE_HOME, FCSZ_STRING_SCORE_HOME);
			FCV_FOULPERIOD = FCI_STRING_TO_INT(FCV_STRING_FOULPERIOD, FCSZ_STRING_FOULPERIOD);
			FCV_MINUTES = FCI_STRING_TO_INT(FCV_STRING_MINUTES, FCSZ_STRING_MINUTES);
			FCV_SECONDS = FCI_STRING_TO_INT(FCV_STRING_SECONDS, FCSZ_STRING_SECONDS);

			//Calculation
			//Calculation:
			//  away_tens = score_away / 10
			//  away_ones = score_away % 10
			//  home_tens = score_home / 10
			//  home_ones = score_home % 10
			FCV_AWAY_TENS = FCV_SCORE_AWAY / 10;
			FCV_AWAY_ONES = FCV_SCORE_AWAY % 10;
			FCV_HOME_TENS = FCV_SCORE_HOME / 10;
			FCV_HOME_ONES = FCV_SCORE_HOME % 10;

			//Calculation
			//Calculation:
			//  home_foul = foulperiod / 100
			//  period = (foulperiod % 100) / 10
			//  away_foul = foulperiod % 10
			FCV_HOME_FOUL = FCV_FOULPERIOD / 100;
			FCV_PERIOD = (FCV_FOULPERIOD % 100) / 10;
			FCV_AWAY_FOUL = FCV_FOULPERIOD % 10;

			//Calculation
			//Calculation:
			//  minute_tens = minutes / 10
			//  minute_ones = minutes % 10
			//  seconds_tens = seconds / 10
			//  seconds_ones = seconds % 10
			FCV_MINUTE_TENS = FCV_MINUTES / 10;
			FCV_MINUTE_ONES = FCV_MINUTES % 10;
			FCV_SECONDS_TENS = FCV_SECONDS / 10;
			FCV_SECONDS_ONES = FCV_SECONDS % 10;

			//Calculation
			//Calculation:
			//  flag = 0
			FCV_FLAG = 0;

			//Call Macro
			//Call Macro: display_all()
			FCM_display_all();

			//Call Macro
			//Call Macro: display_all()
			FCM_display_all();

		// } else {

		}


	}

	//Loop
	//Loop: While 1
	while (1)
	{

		//Calculation
		//Calculation:
		//  score_home = 71
		//  score_away = 89
		//  minutes = 10
		//  seconds = 35
		//  home_foul = 2
		//  away_foul = 4
		//  period = 3
		FCV_SCORE_HOME = 71;
		FCV_SCORE_AWAY = 89;
		FCV_MINUTES = 10;
		FCV_SECONDS = 35;
		FCV_HOME_FOUL = 2;
		FCV_AWAY_FOUL = 4;
		FCV_PERIOD = 3;

		//Calculation
		//Calculation:
		//  away_tens = score_away / 10
		//  away_ones = score_away % 10
		//  home_tens = score_home / 10
		//  home_ones = score_home % 10
		FCV_AWAY_TENS = FCV_SCORE_AWAY / 10;
		FCV_AWAY_ONES = FCV_SCORE_AWAY % 10;
		FCV_HOME_TENS = FCV_SCORE_HOME / 10;
		FCV_HOME_ONES = FCV_SCORE_HOME % 10;

		//Calculation
		//Calculation:
		//  minute_tens = minutes / 10
		//  minute_ones = minutes % 10
		//  seconds_tens = seconds / 10
		//  seconds_ones = seconds % 10
		FCV_MINUTE_TENS = FCV_MINUTES / 10;
		FCV_MINUTE_ONES = FCV_MINUTES % 10;
		FCV_SECONDS_TENS = FCV_SECONDS / 10;
		FCV_SECONDS_ONES = FCV_SECONDS % 10;

		//Call Macro
		//Call Macro: display_score()
		FCM_display_score();

		//Call Macro
		//Call Macro: display_time()
		FCM_display_time();

		//Call Macro
		//Call Macro: foulperiod_display()
		FCM_foulperiod_display();


	}

	//score_data
	//Output: dat -> C3
	trisc = trisc & 0xF7;
	if ((FCV_DAT))
		portc = (portc & 0xF7) | 0x08;
	else
		portc = portc & 0xF7;

	//time_data
	//Output: dat -> C2
	trisc = trisc & 0xFB;
	if ((FCV_DAT))
		portc = (portc & 0xFB) | 0x04;
	else
		portc = portc & 0xFB;

	mainendloop: goto mainendloop;
}

