void FCM_databank()
{

	//Switch
	//Switch: digit?
	switch (FCV_DIGIT)
	{
		case 1:
		{
			//Calculation
			//Calculation:
			//  bits = 48
			FCV_BITS = 48;

			break;
		}
		case 2:
		{
			//Calculation
			//Calculation:
			//  bits = 91
			FCV_BITS = 91;

			break;
		}
		case 3:
		{
			//Calculation
			//Calculation:
			//  bits = 121
			FCV_BITS = 121;

			break;
		}
		case 4:
		{
			//Calculation
			//Calculation:
			//  bits = 116
			FCV_BITS = 116;

			break;
		}
		case 5:
		{
			//Calculation
			//Calculation:
			//  bits = 109
			FCV_BITS = 109;

			break;
		}
		case 6:
		{
			//Calculation
			//Calculation:
			//  bits = 111
			FCV_BITS = 111;

			break;
		}
		case 7:
		{
			//Calculation
			//Calculation:
			//  bits = 56
			FCV_BITS = 56;

			break;
		}
		case 8:
		{
			//Calculation
			//Calculation:
			//  bits = 127
			FCV_BITS = 127;

			break;
		}
		case 9:
		{
			//Calculation
			//Calculation:
			//  bits = 125
			FCV_BITS = 125;

			break;
		}
		case 0:
		{
			//Calculation
			//Calculation:
			//  bits = 63
			FCV_BITS = 63;

			break;
		}
		// default:

	}

}


void FCM_display()
{

	//Calculation
	//Calculation:
	//  digit = tens - 48
	FCV_DIGIT = FCV_TENS - 48;

	//Call Macro
	//Call Macro: databank()
	FCM_databank();

	//Loop
	//Loop: Loop 8 times
	for (FCLV_LOOP1=0; FCLV_LOOP1<8; FCLV_LOOP1++)
	{

		//Calculation
		//Calculation:
		//  dat = bits AND 1
		FCV_DAT = FCV_BITS & 1;

		//data
		//Output: dat -> C2
		trisc = trisc & 0xFB;
		if ((FCV_DAT))
			portc = (portc & 0xFB) | 0x04;
		else
			portc = portc & 0xFB;

		//Delay
		//Delay: 1 us
		delay_us(1);

		//clk
		//Output: 1 -> C0
		trisc = trisc & 0xFE;
		if ((1))
			portc = (portc & 0xFE) | 0x01;
		else
			portc = portc & 0xFE;

		//Delay
		//Delay: 1 us
		delay_us(1);

		//clk
		//Output: 0 -> C0
		trisc = trisc & 0xFE;
		if ((0))
			portc = (portc & 0xFE) | 0x01;
		else
			portc = portc & 0xFE;

		//Delay
		//Delay: 1 us
		delay_us(1);

		//Calculation
		//Calculation:
		//  bits = bits >> 1
		FCV_BITS = FCV_BITS >> 1;


	}

	//Calculation
	//Calculation:
	//  digit = ones - 48
	FCV_DIGIT = FCV_ONES - 48;

	//Call Macro
	//Call Macro: databank()
	FCM_databank();

	//Loop
	//Loop: Loop 8 times
	for (FCLV_LOOP2=0; FCLV_LOOP2<8; FCLV_LOOP2++)
	{

		//Calculation
		//Calculation:
		//  dat = bits AND 1
		FCV_DAT = FCV_BITS & 1;

		//data
		//Output: dat -> C2
		trisc = trisc & 0xFB;
		if ((FCV_DAT))
			portc = (portc & 0xFB) | 0x04;
		else
			portc = portc & 0xFB;

		//Delay
		//Delay: 1 us
		delay_us(1);

		//clk
		//Output: 1 -> C0
		trisc = trisc & 0xFE;
		if ((1))
			portc = (portc & 0xFE) | 0x01;
		else
			portc = portc & 0xFE;

		//Delay
		//Delay: 1 us
		delay_us(1);

		//clk
		//Output: 0 -> C0
		trisc = trisc & 0xFE;
		if ((0))
			portc = (portc & 0xFE) | 0x01;
		else
			portc = portc & 0xFE;

		//Delay
		//Delay: 1 us
		delay_us(1);

		//Calculation
		//Calculation:
		//  bits = bits >> 1
		FCV_BITS = FCV_BITS >> 1;


	}

	//reg
	//Output: 1 -> C1
	trisc = trisc & 0xFD;
	if ((1))
		portc = (portc & 0xFD) | 0x02;
	else
		portc = portc & 0xFD;

	//Delay
	//Delay: 1 us
	delay_us(1);

	//reg
	//Output: 0 -> C1
	trisc = trisc & 0xFD;
	if ((0))
		portc = (portc & 0xFD) | 0x02;
	else
		portc = portc & 0xFD;

	//Delay
	//Delay: 1 us
	delay_us(1);

}


void FCM_Data_recv()
{

	//Calculation
	//Calculation:
	//  rx_count = 0
	FCV_RX_COUNT = 0;

	//Loop
	//Loop: Loop 3 times
	for (FCLV_LOOP3=0; FCLV_LOOP3<3; FCLV_LOOP3++)
	{

		//Call Component Macro
		//Call Component Macro: recieve=ReceiveRS232Char(25)
		FCV_RECIEVE = FCD_RS2320_ReceiveRS232Char(25);

		//Calculation
		//Calculation:
		//  recv[rx_count] = recieve
		//  rx_count = rx_count + 1
		FCV_RECV[FCV_RX_COUNT] = FCV_RECIEVE;
		FCV_RX_COUNT = FCV_RX_COUNT + 1;


	}

	//Decision
	//Decision: recv[1] = 'o'?
	if (FCV_RECV[1] == 'o')
	{

		//
		//Calculation:
		//  tens = recv[0]
		//  ones = recv[2]
		FCV_TENS = FCV_RECV[0];
		FCV_ONES = FCV_RECV[2];

		//Call Component Macro
		//Call Component Macro: SendRS232Char(ones)
		FCD_RS2320_SendRS232Char(FCV_ONES);

		//Call Component Macro
		//Call Component Macro: SendRS232Char(tens)
		FCD_RS2320_SendRS232Char(FCV_TENS);

		//Call Macro
		//Call Macro: display()
		FCM_display();

	// } else {

	}

}



void main()
{
	//Initialization
	ansel = 0;
anselh = 0;

	
		RS232_1_UART_Init( );		//Call initialise function


	//Interrupt initialization code
	option_reg = 0xC0;


	//clk
	//Output: 0 -> C0
	trisc = trisc & 0xFE;
	if ((0))
		portc = (portc & 0xFE) | 0x01;
	else
		portc = portc & 0xFE;

	//reg
	//Output: 0 -> C1
	trisc = trisc & 0xFD;
	if ((0))
		portc = (portc & 0xFD) | 0x02;
	else
		portc = portc & 0xFD;

	//Calculation
	//Calculation:
	//  tens = 9
	//  ones = 9
	FCV_TENS = 9;
	FCV_ONES = 9;

	//Call Macro
	//Call Macro: display()
	FCM_display();

	//Loop
	//Loop: While 1
	while (1)
	{

		//Call Component Macro
		//Call Component Macro: recieve=ReceiveRS232Char(100)
		FCV_RECIEVE = FCD_RS2320_ReceiveRS232Char(100);

		//Decision
		//Decision: recieve = 't'?
		if (FCV_RECIEVE == 't')
		{

			//Call Macro
			//Call Macro: Data_recv()
			FCM_Data_recv();

		// } else {

		}


	}

	mainendloop: goto mainendloop;
}

