void main()
{
	//Initialization
	ansel = 0;
anselh = 0;

	
		RS232_1_UART_Init( );		//Call initialise function


	//Interrupt initialization code
	option_reg = 0xC0;


	//Calculation
	//Calculation:
	//  quant = 3
	FCV_QUANT = 3;

	//Loop
	//Loop: While 1
	while (1)
	{

		//finish
		//Output: 0 -> C6
		trisc = trisc & 0xBF;
		if ((0))
			portc = (portc & 0xBF) | 0x40;
		else
			portc = portc & 0xBF;

		//Connection Point
		//Connection Point: [A]: A
FCC_Main_A:
;

		//Calculation
		//Calculation:
		//  delay = 0
		//  quant = 0
		//  row = 0
		FCV_DELAY = 0;
		FCV_QUANT = 0;
		FCV_ROW = 0;

		//Call Component Macro
		//Call Component Macro: rx=ReceiveRS232Char(100)
		FCV_RX = FCD_RS2320_ReceiveRS232Char(100);

		//Decision
		//Decision: rx = 'R'?
		if (FCV_RX == 'R')
		{

			//Call Component Macro
			//Call Component Macro: rx=ReceiveRS232Char(100)
			FCV_RX = FCD_RS2320_ReceiveRS232Char(100);

			//Decision
			//Decision: rx > '0'AND rx < '5'?
			if (FCV_RX > '0' & FCV_RX < '5')
			{

				//Calculation
				//Calculation:
				//  row = rx - 48
				FCV_ROW = FCV_RX - 48;

				//Call Component Macro
				//Call Component Macro: rx=ReceiveRS232Char(100)
				FCV_RX = FCD_RS2320_ReceiveRS232Char(100);

				//Decision
				//Decision: rx = 'Q'?
				if (FCV_RX == 'Q')
				{

					//Call Component Macro
					//Call Component Macro: rx=ReceiveRS232Char(100)
					FCV_RX = FCD_RS2320_ReceiveRS232Char(100);

					//Decision
					//Decision: rx > '0'AND rx <= '9'?
					if (FCV_RX > '0' & FCV_RX <= '9')
					{

						//Calculation
						//Calculation:
						//  quant = rx - 48
						//  loop = 0
						FCV_QUANT = FCV_RX - 48;
						FCV_LOOP = 0;

						//Switch
						//Switch: row?
						switch (FCV_ROW)
						{
							case 1:
							{
								//finish
								//Output: 1 -> C6
								trisc = trisc & 0xBF;
								if ((1))
									portc = (portc & 0xBF) | 0x40;
								else
									portc = portc & 0xBF;

								//Delay
								//Delay: 2 s
								delay_s(2);

								//Loop
								//Loop: While loop = quant
								while (!(FCV_LOOP == FCV_QUANT))
								{

									//Call Macro
									//Call Macro: row1_forward()
									FCM_row1_forward();

									//Delay
									//Delay: 600 ms
									delay_ms(255);
									delay_ms(255);
									delay_ms(90);

									//Call Macro
									//Call Macro: row1_stop()
									FCM_row1_stop();

									//Delay
									//Delay: 2 s
									delay_s(2);

									//Calculation
									//Calculation:
									//  loop = loop + 1
									FCV_LOOP = FCV_LOOP + 1;


								}

								//Delay
								//Delay: 1 s
								delay_s(1);

								//finish
								//Output: 0 -> C6
								trisc = trisc & 0xBF;
								if ((0))
									portc = (portc & 0xBF) | 0x40;
								else
									portc = portc & 0xBF;

								break;
							}
							case 2:
							{
								//finish
								//Output: 1 -> C6
								trisc = trisc & 0xBF;
								if ((1))
									portc = (portc & 0xBF) | 0x40;
								else
									portc = portc & 0xBF;

								//Delay
								//Delay: 2 s
								delay_s(2);

								//Loop
								//Loop: While loop = quant
								while (!(FCV_LOOP == FCV_QUANT))
								{

									//Call Macro
									//Call Macro: row2_forward()
									FCM_row2_forward();

									//Delay
									//Delay: 600 ms
									delay_ms(255);
									delay_ms(255);
									delay_ms(90);

									//Call Macro
									//Call Macro: row2_stop()
									FCM_row2_stop();

									//Delay
									//Delay: 2 s
									delay_s(2);

									//Calculation
									//Calculation:
									//  loop = loop + 1
									FCV_LOOP = FCV_LOOP + 1;


								}

								//Delay
								//Delay: 1 s
								delay_s(1);

								//finish
								//Output: 0 -> C6
								trisc = trisc & 0xBF;
								if ((0))
									portc = (portc & 0xBF) | 0x40;
								else
									portc = portc & 0xBF;

								break;
							}
							case 3:
							{
								//finish
								//Output: 1 -> C6
								trisc = trisc & 0xBF;
								if ((1))
									portc = (portc & 0xBF) | 0x40;
								else
									portc = portc & 0xBF;

								//Delay
								//Delay: 2 s
								delay_s(2);

								//Loop
								//Loop: While loop = quant
								while (!(FCV_LOOP == FCV_QUANT))
								{

									//Call Macro
									//Call Macro: row3_forward()
									FCM_row3_forward();

									//Delay
									//Delay: 600 ms
									delay_ms(255);
									delay_ms(255);
									delay_ms(90);

									//Call Macro
									//Call Macro: row3_stop()
									FCM_row3_stop();

									//Delay
									//Delay: 2 s
									delay_s(2);

									//Calculation
									//Calculation:
									//  loop = loop + 1
									FCV_LOOP = FCV_LOOP + 1;


								}

								//Delay
								//Delay: 1 s
								delay_s(1);

								//finish
								//Output: 0 -> C6
								trisc = trisc & 0xBF;
								if ((0))
									portc = (portc & 0xBF) | 0x40;
								else
									portc = portc & 0xBF;

								break;
							}
							case 4:
							{
								//finish
								//Output: 1 -> C6
								trisc = trisc & 0xBF;
								if ((1))
									portc = (portc & 0xBF) | 0x40;
								else
									portc = portc & 0xBF;

								//Delay
								//Delay: 2 s
								delay_s(2);

								//Loop
								//Loop: While loop = quant
								while (!(FCV_LOOP == FCV_QUANT))
								{

									//Call Macro
									//Call Macro: row4_forward()
									FCM_row4_forward();

									//Delay
									//Delay: 600 ms
									delay_ms(255);
									delay_ms(255);
									delay_ms(90);

									//Call Macro
									//Call Macro: row4_stop()
									FCM_row4_stop();

									//Delay
									//Delay: 2 s
									delay_s(2);

									//Calculation
									//Calculation:
									//  loop = loop + 1
									FCV_LOOP = FCV_LOOP + 1;


								}

								//Delay
								//Delay: 1 s
								delay_s(1);

								//finish
								//Output: 0 -> C6
								trisc = trisc & 0xBF;
								if ((0))
									portc = (portc & 0xBF) | 0x40;
								else
									portc = portc & 0xBF;

								break;
							}
							default:
							{
								//Goto Connection Point
								//Goto Connection Point: [A]: A
								goto FCC_Main_A;

							}
						}

					} else {

						//Goto Connection Point
						//Goto Connection Point: [A]: A
						goto FCC_Main_A;

					}

				} else {

					//Goto Connection Point
					//Goto Connection Point: [A]: A
					goto FCC_Main_A;

				}

			} else {

				//Goto Connection Point
				//Goto Connection Point: [A]: A
				goto FCC_Main_A;

			}

		// } else {

		}


	}

	mainendloop: goto mainendloop;
}

